mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT FAQNUM , DECODE(FAQCODE,'1','회원','2','주문/결제','3','상품/배송','4','취소/교환/환불','5','중고','6','셔플','기타') FAQCODE , FAQTITLE , FAQCONT , FAQANS FROM FAQ_BOARD WHERE 1=1
AND FAQCODE = #{faq_page_no}
) A ) B WHERE RN BETWEEN '${firstIndex}' AND '${lastIndex}'

mapper.update:
	UPDATE REVIEW_BOARD 
		SET
			 REVGRADE  = #{revgrade}
			,REVCONT    = #{revcont}
			,REVFILENAME = #{revfilename}
  	WHERE  
		REVNUM = #{revnum}
